var arr = []
var date = flow.get('sort_date') || undefined

if(date == undefined || date == false)
{
    msg.payload = msg.payload.feeds
    return msg
}

for (var i = 0; i < msg.payload.feeds.length; i++)
{
    if (isNaN(Date.parse(msg.payload.feeds[i].field3)))
        continue
    var tempDate = new Date(Date.parse(msg.payload.feeds[i].field3))
    tempDate.setHours(tempDate.getHours() - 7)
    if (tempDate.toDateString() == date.toDateString())
        arr.push(msg.payload.feeds[i])
}
msg.payload = arr
return msg;

